package org.algo.sort;

public class PrintArray {

	public static void printIntArr(int[] arr){
		System.out.print("[ ");
		for(int num:arr){
			System.out.print(num+" ");
		}
		System.out.println(" ]");
	}

	public static void printIntArr(float[] arr) {
		// TODO Auto-generated method stub
		System.out.print("[ ");
		for(float num:arr){
			System.out.print(num+" ");
		}
		System.out.println(" ]");
	}

	public static void printIntArr(char[] arr) {
		// TODO Auto-generated method stub
				System.out.print("[ ");
				for(char ch:arr){
					System.out.print(ch+" ");
				}
				System.out.println(" ]");
	}
}
